/* Background image for post previews */ .excerpt_bg { background-size: 120%; background-position: center center; background-repeat: no-repeat; padding: 20px; border-radius: 8px; position: relative; min-height: 120px; }
.excerpt_bg::before { content: ''; position: absolute; top: 0; left: 0; right: 0; bottom: 0; background: rgba(0, 0, 0, 0.6); border-radius: 8px; }
.excerpt_bg .excerpt_header, .excerpt_bg .excerpt_footer, .excerpt_bg .pale { position: relative; z-index: 2; color: white; }
.excerpt_bg .post_link a { color: white; }

